mapper.select:
SELECT *
FROM member


		MEMBER_ID LIKE #{member_id}


		AND PASSWORD LIKE #{password}

mapper.update:
	  UPDATE member SET

    		NICKNAME = #{nickname}


    		PASSWORD = #{password}


    		PHONE = #{phone}


    		PROFILEIMG = #{profileimg}

	  WHERE MEMBER_ID LIKE #{member_id}

mapper.delete:
DELETE FROM member
WHERE MEMBER_ID = #{member_id}
AND PASSWORD = #{password}
